import "./Ownable.sol";
pragma solidity 0.5.12;

contract CoinFlip is Ownable{

    event coinFlipped(address);
    event ammountPaidToPlayer(uint);

    address public owner;
    uint private balance;
    uint resultOfGame;
    uint valutToPlayer;

    function random() public view returns(uint){
        return now % 2;
    }

    function depositBalance() public payable {
        balance += msg.value;
    }

    function flipCoin() public payable {
        require(balance >= msg.value * 2, "Balance needs to have at least twice as ETH as guest play");
        require(msg.value !=0, "Needs to send positive balance");
        if (now % 2 == 0){
            msg.sender.transfer(2*msg.value);
            balance -= (msg.value*2);
            resultOfGame = (msg.value*2);
        }
        else{
            balance += msg.value;
            resultOfGame = 0;
        }
        emit coinFlipped(msg.sender);
        if (resultOfGame != 0) emit ammountPaidToPlayer(msg.value*2);
    }

    function getResultOfGame() public view returns (uint){
        return resultOfGame;
    }

    function getBalance() public view returns (uint) {
        return balance;
    }

    function withdrawAll () public onlyOwner returns(uint) {
        uint toTransfer = balance;
        balance = 0;
        msg.sender.transfer(toTransfer);
        return toTransfer;
    }
}
